import React, { useState } from 'react';
import { CloudUploadIcon } from '@heroicons/react/outline';
import axios from 'axios';

function UploadPage() {
  const [file, setFile] = useState(null);
  const [vaga, setVaga] = useState('');
  const [resposta, setResposta] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      alert('Por favor, selecione um arquivo!');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    // Poderíamos adicionar vaga também no futuro: formData.append('vaga', vaga);

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload-curriculo/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Resposta do servidor:', response.data);
      setResposta(response.data);
      alert(`Currículo enviado! Nome recebido: ${response.data.nome_arquivo}`);
    } catch (error) {
      console.error('Erro ao enviar currículo:', error);
      alert('Erro ao enviar currículo.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-indigo-200 via-purple-200 to-pink-200 p-6">
      <form onSubmit={handleSubmit} className="bg-white p-10 rounded-3xl shadow-2xl w-full max-w-xl animate-fadeIn">
        <h1 className="text-4xl font-extrabold text-center text-indigo-600 mb-8 tracking-tight">
          Upload de Currículo
        </h1>

        <div className="mb-6">
          <label className="block text-gray-700 mb-2 text-sm font-semibold">
            Selecione seu currículo:
          </label>
          <div className="flex items-center justify-center w-full">
            <label className="flex flex-col items-center px-4 py-6 bg-indigo-50 text-indigo-700 rounded-lg shadow-md tracking-wide uppercase border border-indigo-300 cursor-pointer hover:bg-indigo-100 transition duration-300 ease-in-out">
              <CloudUploadIcon className="w-8 h-8 mb-2" />
              <span className="text-base leading-normal">Selecione um arquivo</span>
              <input 
                type="file"
                accept=".pdf,.doc,.docx"
                onChange={(e) => setFile(e.target.files[0])}
                className="hidden"
              />
            </label>
          </div>
        </div>

        <div className="mb-8">
          <label className="block text-gray-700 mb-2 text-sm font-semibold">
            Descrição da vaga:
          </label>
          <textarea 
            value={vaga}
            onChange={(e) => setVaga(e.target.value)}
            className="w-full p-4 border border-gray-300 rounded-lg h-36 resize-none focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
            placeholder="Cole aqui o texto da vaga..."
          />
        </div>

        <button 
          type="submit"
          className="w-full flex justify-center items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg transition duration-300"
        >
          <CloudUploadIcon className="w-5 h-5" />
          Gerar Currículo Inteligente
        </button>

        {resposta && (
          <div className="mt-8 p-4 bg-green-100 text-green-700 rounded-lg shadow">
            <h2 className="font-bold mb-2">Resposta do servidor:</h2>
            <p><strong>Nome do arquivo:</strong> {resposta.nome_arquivo}</p>
            <p><strong>Tamanho:</strong> {resposta.tamanho_bytes} bytes</p>
            <div className="mt-4 p-4 bg-white text-black rounded-lg shadow-inner overflow-y-auto max-h-96">
              <h3 className="font-bold mb-2">Texto extraído do currículo:</h3>
              <pre className="whitespace-pre-wrap">{resposta.conteudo_texto}</pre>
            </div>
          </div>
        )}
      </form>
    </div>
  );
}

export default UploadPage;
