import React, { useState } from 'react';

function UploadPage() {
  const [file, setFile] = useState(null);
  const [vaga, setVaga] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Arquivo:', file);
    console.log('Descrição da vaga:', vaga);
    alert('Currículo enviado! (simulação)');
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-indigo-100 via-purple-100 to-pink-100 p-6">
      <form onSubmit={handleSubmit} className="bg-white p-10 rounded-2xl shadow-2xl w-full max-w-lg">
        <h1 className="text-3xl font-extrabold text-center text-indigo-600 mb-8">
          Upload de Currículo
        </h1>

        <div className="mb-6">
          <label className="block text-gray-700 mb-2 text-sm font-semibold">
            Selecione seu currículo:
          </label>
          <input 
            type="file" 
            accept=".pdf,.doc,.docx"
            onChange={(e) => setFile(e.target.files[0])}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
          />
        </div>

        <div className="mb-8">
          <label className="block text-gray-700 mb-2 text-sm font-semibold">
            Descrição da vaga:
          </label>
          <textarea 
            value={vaga}
            onChange={(e) => setVaga(e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg h-32 resize-none focus:outline-none focus:ring-2 focus:ring-indigo-400"
            placeholder="Cole aqui o texto da vaga..."
          />
        </div>

        <button 
          type="submit"
          className="w-full bg-indigo-600 text-white py-3 rounded-lg font-bold hover:bg-indigo-700 transition duration-300"
        >
          Gerar Currículo Inteligente
        </button>
      </form>
    </div>
  );
}

export default UploadPage;
